package practice1;

public class Mathfunctions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.pow(2, 5)+" power");
		System.out.println(Math.max(5, 10)+"Max ");
		System.out.println(Math.min(2, 5)+"Min");
		System.out.println(Math.sin(4));
		System.out.println(Math.cos(90));
		System.out.println(Math.tan(45));
		System.out.println(Math.log(4));
		System.out.println(Math.floor(3.87));
		System.out.println(Math.ceil(7.9));
		System.out.println(Math.abs(-12));
		System.out.println(Math.sqrt(25));
		System.out.println(Math.exp(9));

	}

}
